    import IncrementCount from "./components/IncrementCount";
    import DecreaseCount from "./components/DecreaseCount";
    import { EventBus } from "./eventBus.js";
    export default {
        name: "App",
        components: {
            IncrementCount,
            DecreaseCount
        },
        data() {
            return {
                degValue: 0,
                fontCount: 0,
                backCount: 0
            }
        },
        mounted() {
            EventBus.$on("incremented", ({num, deg}) => {
                this.fontCount += num
                this.$nextTick(() => {
                    this.backCount += num
                    this.degValue += deg;
                })
            })
            EventBus.$on("decreased", ({num, deg}) => {
                this.fontCount -= num
                this.$nextTick(() => {
                    this.backCount -= num
                    this.degValue -= deg
                })
            })
        }
    }


